import ref = require('ref');
import equal = require('common/directives/equal_directive');
import gridmenu = require('common/directives/gridmenu_directive');
import resetform = require('common/directives/reset_form_directive');
import lightbox = require('common/directives/lightbox_directive');

export class Directives {
    constructor(module:ng.IModule) {

        module.directive(equal.EqualSomeDirective._name, equal.EqualSomeDirective.directive);
        module.directive(gridmenu.GridMenuDirective._name, gridmenu.GridMenuDirective.directive);
        module.directive(resetform.FormDirective._name, resetform.FormDirective.directive);
        module.directive(resetform.NgFormDirective._name, resetform.NgFormDirective.directive);
        module.directive(resetform.NgModelDirective._name, resetform.NgModelDirective.directive);

        module.controller(lightbox.LightboxController._name, lightbox.LightboxController);
        module.provider(lightbox.LightboxProvider._name, lightbox.LightboxProvider.provider);
        module.directive(lightbox.LightboxDirective._name, lightbox.LightboxDirective.directive);

        //module.directive(root.RootDirective._name, root.RootDirective.directive);
    }
}