import ref = require('ref');
import angular = require('angular');
import angularCommon = require('common/common/angular_common');

export class stringDate extends angularCommon.AngularCommon {
    public static _name:string = 'stringDate';
    public static filter:Array<any> = ['$filter', ($filter)=> {
        return (val, refres):string => {
            var dateFilter = $filter("date");
            var date = new Date(val);

            return dateFilter(date, refres);
        };
    }];
}

//filterModule.module.filter('stringDate', stringDate.filter);