import cd = require('common/models/common/base_data');
import bm = require('common/models/dashboard/bas_model');

export module Dashboard {
    /*
     * 同好会申请模型
     * */
    export class BasRecommendModel extends cd.Common.BaseData {
        public recommendId:number;
        public basData:bm.Dashboard.BasModel;
        public status:number;

        public static _convert(data:any){
            var model = new Dashboard.BasRecommendModel({
                recommendId: data.id,
                status: data.status,
                createAt: data.created_at,
                updateAt: data.updated_at,
                basData: bm.Dashboard.BasModel._convert(data)
            });
            model.basData.baId = data.ba_id;

            return model;
        }

        constructor(data?:any) {
            super(data);
        }
    }
}